App.module.templates['controls\textarea\textarea.template.js']="<label class=\"textarea\">\r\n    <span class=\"form__label\">{{label}}</span>\r\n    <textarea ng-model=\"ngModel\" maxlength=\"{{max}}\" rows=\"{{rows || 8}}\" class=\"textarea__control\" ></textarea>\r\n    <span ng-if=\"max && max - ngModel.length == 1\" class=\"textarea__max\"><em>{{max - ngModel.length}}</em> character</span>\r\n    <span ng-if=\"max && max - ngModel.length != 1\" class=\"textarea__max\"><em>{{max - ngModel.length}}</em> characters</span>\r\n</label>";